import axios from 'axios';
import React, { useState } from 'react';




const SignUpForm = () => {
    const [pseudo, setPseudo] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [controlPassword, setControlPassword] = useState('');

    //on va aller chercher toute les erreurs possibles ici 
const handleRegister = async (e) => {
    e.preventDefault();
    const terms = document.getElementById('terms');
    const pseudoError = document.querySeclector('.pseudo.error');
    const emailError = document.querySeclector('.email.error');
    const passwordError = document.querySeclector('.password.error');
    const passwordConfirmError = document.querySeclector('.password-confirm.error');

}

    return (
<form action="" onSubmit={handleRegister} id="sign-up-form">
<label htmlFor='pseudo'>Pseudo</label>
    <br/>
    <input
     type="text"
      name="pseudo" 
      id="pseudo"
      onChange={(e) => setPseudo (e.target.value)}
      value={pseudo}
      />
      <div className='pseudo error'> </div>
      <br/>

      <label htmlFor='email'>Email</label>
    <br/>
    <input
     type="text"
      name="email" 
      id="email"
      onChange={(e) => setEmail (e.target.value)}
      value={email}
      />
      <div className='emailerror'> </div>
      <br/>

      <label htmlFor='password'>Mot de passe</label>
    <br/>
    <input
     type="password"
      name="password" 
      id="password"
      onChange={(e) => setPassword(e.target.value)}
      value={password}
      />
      <div className='password error'> </div>
      <br/>

      <label htmlFor='password-conf'>Confirmer le mot de passe</label>
    <br/>
    <input
     type="password"
      name="password" 
      id="password-conf"
      onChange={(e) => setControlPassword (e.target.value)}
      value={controlPassword}
      />
      <div className='password-confirm error'> </div>
      <br/>

      <input type="checkbox" id="terms" />

      <label htmlFor='terms'>J'accepte les <a href="/" target="blank" 
      rel="noopener noreferrer">  Conditions générales </a></label>
      <div classsName="terms error"></div>
<br/>


    <input type="submit" value="Valider inscription" />
</form>

    );
};

export default SignUpForm;
